import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const departmentNames = {
  1: 'IT',
  2: 'HR',
  3: 'Finance'
};

const TicketsPage = () => {
  const [tickets, setTickets] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTickets = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:62423/api/Tickets', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTickets(res.data);
      } catch (err) {
        console.error('Failed to fetch tickets:', err);
        setError('Failed to load tickets. Please try again later.');
      }
    };
    fetchTickets();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Tickets</h1>

      {error && <div className="text-red-600 mb-4">{error}</div>}

      <div className="grid gap-4">
        {tickets.map(ticket => (
          <Link key={ticket.id} to={`/tickets/${ticket.id}`} className="block p-4 bg-white shadow rounded hover:bg-gray-100">
            <h2 className="font-semibold text-lg">{ticket.title}</h2>
            <p className="text-sm">Status: {ticket.status} | Severity: {ticket.severity}</p>
            <p className="text-sm text-gray-600">Department: {departmentNames[ticket.departmentId] || 'Unknown'}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TicketsPage;
