import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TicketDetailsPage = () => {
  const { id } = useParams();
  const ticketId = id?.split(':')[0];

  const [ticket, setTicket] = useState(null);
  const [remarks, setRemarks] = useState([]);
  const [newRemark, setNewRemark] = useState("");

  const username = localStorage.getItem("username");
  const allowedUsers = ['supervisor1', 'officer1', 'junior1o', 'admin'];

  const canViewRemarks = allowedUsers.includes(username);

  const canActOnTicket = () => {
    if (!ticket) return false;

    const isCritical = ticket.severity === "Critical";
    const isAssigned = ticket.assignedTo === username;

    if (!allowedUsers.includes(username)) return false;

    if ((username === 'officer1' || username === 'junior1o') && isCritical && !isAssigned) {
      return false;
    }

    return true;
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const ticketRes = await axios.get(`https://localhost:62423/api/Tickets/${ticketId}`);
        setTicket(ticketRes.data);

        if (canViewRemarks) {
          const remarksRes = await axios.get(`https://localhost:62423/api/Remarks/${ticketId}`);
          setRemarks(remarksRes.data);
        }
      } catch (error) {
        console.error("Failed to fetch ticket or remarks:", error);
      }
    };
    fetchData();
  }, [ticketId]);

  const addRemark = async () => {
    if (!canActOnTicket()) {
      alert("You are not allowed to add remarks to this Critical ticket unless it's assigned to you.");
      return;
    }

    try {
      await axios.post(`https://localhost:62423/api/Remarks`, {
        ticketId: ticketId,
        content: newRemark
      });
      setNewRemark("");
      const updated = await axios.get(`https://localhost:62423/api/Remarks/${ticketId}`);
      setRemarks(updated.data);
    } catch (error) {
      console.error("Failed to add remark:", error);
    }
  };

  if (!ticket) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{ticket.title}</h1>
      <p className="mb-2">{ticket.description}</p>
      <p><strong>Status:</strong> {ticket.status}</p>
      <div className="mb-2">
        <label className="font-semibold">Severity:</label>
        <input
          type="text"
          value={ticket.severity}
          readOnly
          className="w-full p-2 border bg-gray-100 rounded"
        />
      </div>
      <div className="mb-4">
        <label className="font-semibold">Department:</label>
        <input
          type="text"
          value={ticket.department}
          readOnly
          className="w-full p-2 border bg-gray-100 rounded"
        />
      </div>

      {/* Remarks Section */}
      {canViewRemarks && (
        <>
          <h2 className="mt-4 text-lg font-semibold">Remarks</h2>
          <ul className="mb-4">
            {remarks.map(r => (
              <li key={r.id} className="border-b py-2">
                {r.content} <span className="text-xs text-gray-500">({new Date(r.createdAt).toLocaleString()})</span>
              </li>
            ))}
          </ul>

          {canActOnTicket() && (
            <>
              <textarea
                value={newRemark}
                onChange={(e) => setNewRemark(e.target.value)}
                className="w-full p-2 border rounded mb-2"
                rows="3"
                placeholder="Add a remark..."
              />
              <button
                onClick={addRemark}
                className="bg-blue-500 text-white px-4 py-2 rounded"
              >
                Add Remark
              </button>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default TicketDetailsPage;
