import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreateTicketPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [severity, setSeverity] = useState('Low');
  const [status, setStatus] = useState('Open');
  const [departmentId, setDepartmentId] = useState(1);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.post('http://localhost:62423/api/Tickets', {
        title,
        description,
        severity,
        status,
        departmentId
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate('/tickets');
    } catch (err) {
      console.error('Failed to create ticket:', err);
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Create Ticket</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} className="w-full p-2 border rounded" required />
        <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} className="w-full p-2 border rounded" rows="4" required />
        <div className="flex space-x-4">
          <div>
            <label className="block font-semibold">Severity</label>
            <select value={severity} onChange={(e) => setSeverity(e.target.value)} className="p-2 border rounded">
              <option>Low</option>
              <option>Medium</option>
              <option>High</option>
              <option>Critical</option>
            </select>
          </div>
          <div>
            <label className="block font-semibold">Status</label>
            <select value={status} onChange={(e) => setStatus(e.target.value)} className="p-2 border rounded">
              <option>Open</option>
              <option>In Progress</option>
              <option>Resolved</option>
              <option>Closed</option>
            </select>
          </div>
        </div>
        <div>
          <label className="block font-semibold">Department ID</label>
          <input type="number" value={departmentId} onChange={(e) => setDepartmentId(e.target.value)} className="p-2 border rounded w-full" required />
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      </form>
    </div>
  );
};

export default CreateTicketPage;
