import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const TicketsPage = () => {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:62423/api/Tickets', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTickets(res.data);
    };
    fetchTickets();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Tickets</h1>
      <div className="grid gap-4">
        {tickets.map(ticket => (
          <Link key={ticket.id} to={`/tickets/${ticket.id}`} className="block p-4 bg-white shadow rounded hover:bg-gray-100">
            <h2 className="font-semibold">{ticket.title}</h2>
            <p>Status: {ticket.status} | Severity: {ticket.severity}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TicketsPage;
