import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TicketDetailsPage = () => {
  const { id } = useParams();
  const ticketId = id?.split(':')[0];
  const username = localStorage.getItem('username');
  const privilegedUsers = ['supervisor1', 'officer1', 'junior1', 'admin'];

  const [ticket, setTicket] = useState(null);
  const [remarks, setRemarks] = useState([]);
  const [newRemark, setNewRemark] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const ticketRes = await axios.get(`https://localhost:62423/api/Tickets/${ticketId}`);
        setTicket(ticketRes.data);

        if (privilegedUsers.includes(username)) {
          const remarksRes = await axios.get(`https://localhost:62423/api/Remarks/${ticketId}`);
          setRemarks(remarksRes.data);
        }
      } catch (error) {
        console.error("Failed to fetch ticket or remarks:", error);
      }
    };
    fetchData();
  }, [ticketId, username]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicket(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    if (!['Low', 'Medium', 'High'].includes(ticket.severity)) {
      setError("Severity must be Low, Medium, or High.");
      return;
    }

    try {
      await axios.put(`https://localhost:62423/api/Tickets/${ticketId}`, ticket);
      alert("Ticket updated successfully");
    } catch (err) {
      console.error("Error updating ticket:", err);
    }
  };

  const addRemark = async () => {
    try {
      await axios.post(`https://localhost:62423/api/Remarks`, {
        ticketId,
        content: newRemark
      });
      setNewRemark("");
      const updated = await axios.get(`https://localhost:62423/api/Remarks/${ticketId}`);
      setRemarks(updated.data);
    } catch (error) {
      console.error("Failed to add remark:", error);
    }
  };

  if (!ticket) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Ticket Details</h1>

      <div className="mb-3">
        <label className="font-semibold">Title:</label>
        <input
          type="text"
          name="title"
          value={ticket.title}
          onChange={handleInputChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div className="mb-3">
        <label className="font-semibold">Description:</label>
        <textarea
          name="description"
          value={ticket.description}
          onChange={handleInputChange}
          className="w-full p-2 border rounded"
          rows="3"
        />
      </div>

      <div className="mb-3">
        <label className="font-semibold">Severity:</label>
        <input
          type="text"
          name="severity"
          value={ticket.severity}
          onChange={handleInputChange}
          className="w-full p-2 border rounded"
        />
        {error && <p className="text-red-500 text-sm">{error}</p>}
      </div>

      <div className="mb-4">
        <label className="font-semibold">Department:</label>
        <input
          type="text"
          value={ticket.department}
          readOnly
          className="w-full p-2 border bg-gray-100 text-gray-700 rounded cursor-not-allowed"
        />
      </div>

      <button
        onClick={handleSave}
        className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded mb-4"
      >
        Save Changes
      </button>

      {/* Remarks section: show only to privileged users */}
      {privilegedUsers.includes(username) && (
        <>
          <h2 className="text-lg font-semibold mb-2">Remarks</h2>
          <ul className="mb-4">
            {remarks.map(r => (
              <li key={r.id} className="border-b py-2">
                {r.content} <span className="text-xs text-gray-500">({r.createdAt})</span>
              </li>
            ))}
          </ul>

          <textarea
            value={newRemark}
            onChange={(e) => setNewRemark(e.target.value)}
            className="w-full p-2 border rounded mb-2"
            rows="3"
          />
          <button
            onClick={addRemark}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add Remark
          </button>
        </>
      )}
    </div>
  );
};

export default TicketDetailsPage;
