import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Dashboard</h1>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
        <Link to="/create-ticket" className="bg-green-100 hover:bg-green-200 p-4 rounded shadow text-center">
          <span className="text-green-700 font-semibold">Create Ticket</span>
        </Link>
        <Link to="/tickets" className="bg-blue-100 hover:bg-blue-200 p-4 rounded shadow text-center">
          <span className="text-blue-700 font-semibold">View Tickets</span>
        </Link>
        <button onClick={handleLogout} className="bg-red-100 hover:bg-red-200 p-4 rounded shadow text-center w-full">
          <span className="text-red-700 font-semibold">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
