import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TicketDetailsPage = () => {
  const { id } = useParams();
  const [ticket, setTicket] = useState(null);
  const [remarks, setRemarks] = useState([]);
  const [newRemark, setNewRemark] = useState("");

  useEffect(() => {
    const token = localStorage.getItem('token');
    const fetchData = async () => {
      const ticketRes = await axios.get(`http://localhost:62423/api/Tickets/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTicket(ticketRes.data);

      const remarksRes = await axios.get(`http://localhost:62423/api/Remarks/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRemarks(remarksRes.data);
    };
    fetchData();
  }, [id]);

  const addRemark = async () => {
    const token = localStorage.getItem('token');
    await axios.post(`http://localhost:62423/api/Remarks`, {
      ticketId: id,
      content: newRemark
    }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setNewRemark("");
    window.location.reload(); // simple reload to show new remarks
  };

  if (!ticket) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{ticket.title}</h1>
      <p className="mb-2">{ticket.description}</p>
      <p>Status: {ticket.status}, Severity: {ticket.severity}</p>
      <h2 className="mt-4 text-lg font-semibold">Remarks</h2>
      <ul className="mb-4">
        {remarks.map(r => (
          <li key={r.id} className="border-b py-2">{r.content} <span className="text-xs text-gray-500">({r.createdAt})</span></li>
        ))}
      </ul>
      <textarea value={newRemark} onChange={(e) => setNewRemark(e.target.value)} className="w-full p-2 border rounded mb-2" rows="3" />
      <button onClick={addRemark} className="bg-blue-500 text-white px-4 py-2 rounded">Add Remark</button>
    </div>
  );
};

export default TicketDetailsPage;
