import React from 'react';
import { Link } from 'react-router-dom';
import { PlusCircle, ClipboardList, LogOut } from 'lucide-react';

const Dashboard = () => {
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h1 className="text-4xl font-bold text-center mb-10 text-gray-800">Welcome to the Helpdesk Dashboard</h1>

      <div className="grid grid-cols-1 sm:grid-cols-3 gap-8">
        <Link
          to="/create-ticket"
          className="bg-white border border-green-200 hover:border-green-400 hover:shadow-lg transition-all p-6 rounded-xl flex flex-col items-center text-center"
        >
          <PlusCircle className="text-green-600 mb-3" size={36} />
          <span className="text-green-700 font-semibold text-lg">Create Ticket</span>
        </Link>

        <Link
          to="/tickets"
          className="bg-white border border-blue-200 hover:border-blue-400 hover:shadow-lg transition-all p-6 rounded-xl flex flex-col items-center text-center"
        >
          <ClipboardList className="text-blue-600 mb-3" size={36} />
          <span className="text-blue-700 font-semibold text-lg">View Tickets</span>
        </Link>

        <button
          onClick={handleLogout}
          className="bg-white border border-red-200 hover:border-red-400 hover:shadow-lg transition-all p-6 rounded-xl flex flex-col items-center text-center w-full"
        >
          <LogOut className="text-red-600 mb-3" size={36} />
          <span className="text-red-700 font-semibold text-lg">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
